import { createSelector } from '@reduxjs/toolkit'
import ContactAddForm from './ContactAddForm/ContactAddForm'
import ContactList from './ContactList/ContactList'
import Filter from './Filter/Filter'
import { useSelector } from 'react-redux'
import { useGetContactsQuery } from '../redux/contacts/apiService'
import { Spinner } from './Spinner/Spinner'
import { Toaster } from 'react-hot-toast'
import { filterSelector } from '../redux/contacts/contacts-selectors'

export default function App() {
  const { data, isFetching } = useGetContactsQuery()

  // const filter = useSelector(filterSelector)

  const getFilteredContacts = createSelector(
    [useGetContactsQuery, useSelector(filterSelector)],
    (data, filter) => {
      data.filter((contact) => contact.name.toLowerCase().includes(filter))
    },
  )

  return (
    <>
      <h2>Phonebook</h2>

      <ContactAddForm />

      <h2>Contacts</h2>

      <Filter />

      {isFetching && <Spinner />}

      {data && <ContactList contacts={getFilteredContacts}></ContactList>}

      <Toaster />
    </>
  )
}
