// import { useState } from 'react'
import ContactAddFormEl from './ContactAddForm.styled'
// import PropTypes from 'prop-types'
// import { useSelector } from 'react-redux'
import { useGetContactsQuery } from '../../redux/contacts/apiService'
// import { v4 as uuidv4 } from 'uuid'
import { useAddContactMutation } from '../../redux/contacts/apiService'
import { toast } from 'react-hot-toast'

const styles = {
  input: {
    display: 'block',
    marginBottom: '10px',
    marginTop: '5px',
  },
  label: {
    marginBottom: '10px',
  },
  button: {
    width: '100px',
    fontSize: '12px',
    backgroundColor: 'white',
    borderRadius: '5px',
    border: '1px solid gray',
    cursor: 'pointer',
  },
}

export default function ContactAddForm() {
  const [addContact, { isLoading }] = useAddContactMutation()
  const { data } = useGetContactsQuery()
  // console.log(data)

  // const [name, setName] = useState('')
  // const [number, setNumber] = useState('')

  // const dispatch = useDispatch()
  // const contacts = useSelector(contactsSelector)

  const handleSubmit = (e) => {
    e.preventDefault()
    const contactsHaveDuplicate = data.find(
      (data) => data.name === e.target.elements.name.value,
    )

    if (contactsHaveDuplicate) {
      alert(`${e.target.elements.name.value} is already in contacts`)
      toast.error('Contact not added')
    } else {
      addContact({
        name: e.target.elements.name.value,
        number: e.target.elements.number.value,
      })
      toast.success('Contact added')
    }

    // onSubmit(name, number)
    // setName('')
    // setNumber('')
    // dispatch(
    //   addContact({
    //     id: uuidv4(),
    // name: e.target.elements.name.value,
    // number: e.target.elements.number.value,
    //   }),
    // )
    e.target.reset()
  }

  // const handleChange = (e) => {
  //   const { name, value } = e.target
  //   switch (name) {
  //     case 'name':
  //       setName(value)
  //       break
  //     case 'number':
  //       setNumber(value)
  //       break
  //     default:
  //       return
  //   }
  // }

  return (
    <>
      <ContactAddFormEl onSubmit={handleSubmit}>
        <label style={styles.label}>
          Name
          <input
            style={styles.input}
            type="text"
            name="name"
            pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
            title="Имя может состоять только из букв, апострофа, тире и пробелов. Например Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan и т. п."
            required
            // value={name}
            // onChange={handleChange}
          />
        </label>
        <label style={styles.label}>
          Number
          <input
            style={styles.input}
            type="tel"
            name="number"
            required
            // value={number}
            // onChange={handleChange}
          ></input>
        </label>
        <button type="submit" style={styles.button} disabled={isLoading}>
          Add Contact
        </button>
      </ContactAddFormEl>
    </>
  )
}

