import { createSlice } from '@reduxjs/toolkit'

const initialState = {
  filter: '',
}

export const filterSlice = createSlice({
  name: 'filter',
  initialState,
  reducers: {
    filterContact(state, action) {
      state.filter = action.payload
    },
  },
})

export const {
  //   addContact,
  //   deleteContact,
  filterContact,
} = filterSlice.actions

export const filterReducer = filterSlice.reducer

