import { Component } from 'react'
import { v4 as uuidv4 } from 'uuid'
import ContactAddForm from './ContactAddForm/ContactAddForm'

export default class App extends Component {
  state = {
    contacts: [
      { id: 'id-1', name: 'Rosie Simpson', number: '459-12-56' },
      { id: 'id-2', name: 'Hermione Kline', number: '443-89-12' },
      { id: 'id-3', name: 'Eden Clements', number: '645-17-79' },
      { id: 'id-4', name: 'Annie Copeland', number: '227-91-26' },
    ],
    filter: '',
    name: '',
    number: '',
  }

  handleNameChange = ({ target }) => {
    const nameInput = target.value
    this.setState({
      name: nameInput,
    })
  }

  handlePhoneChange = ({ target }) => {
    const PhoneInput = target.value
    this.setState({
      number: PhoneInput,
    })
  }

  handleFilterChange = ({ target }) => {
    const filterInput = target.value
    this.setState({
      filter: filterInput,
    })
  }

  getFilteredNames = () => {
    const { filter, contacts } = this.state
    const normilizedFilterState = filter.toLowerCase()

    return contacts.filter((contact) =>
      contact.name.toLowerCase().includes(normilizedFilterState),
    )
  }

  handleSubmit = (e) => {
    e.preventDefault()
    this.setState((prevState) => ({
      contacts: [
        ...prevState.contacts,
        { name: this.state.name, id: uuidv4(), number: this.state.number },
      ],
    }))
    this.setState({
      name: '',
      number: '',
    })
  }

  render() {
    const { name, number, filter } = this.state

    const filteredContactNames = this.getFilteredNames()

    return (
      <>
        <h2>Phonebook</h2>
        <ContactAddForm />
        {/* <form onSubmit={this.handleSubmit}>
          <label>
            Name
            <input
              type="text"
              name="name"
              pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
              title="Имя может состоять только из букв, апострофа, тире и пробелов. Например Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan и т. п."
              required
              value={name}
              onChange={this.handleNameChange}
            />
          </label>
          <label>
            Number
            <input
              type="tel"
              name="tel"
              required
              value={number}
              onChange={this.handlePhoneChange}
            ></input>
          </label>
          <button>Add Contact</button>
        </form> */}

        <h2>Contacts</h2>

        <label>
          Find contacts by name
          <input
            type="text"
            name="filter"
            value={filter}
            onChange={this.handleFilterChange}
          ></input>
        </label>

        <ul>
          {filteredContactNames.map((contact) => (
            <li key={contact.id}>
              {contact.name}: {contact.number}
            </li>
          ))}
        </ul>
      </>
    )
  }
}
