import React, { Component } from 'react'
import ContactAddFormEl from './ContactAddForm.styled'
import { v4 as uuidv4 } from 'uuid'

export default class ContactAddForm extends Component {
  state = {
    name: '',
    number: '',
  }
  handleNameChange = ({ target }) => {
    const nameInput = target.value
    this.setState({
      name: nameInput,
    })
  }

  handlePhoneChange = ({ target }) => {
    const PhoneInput = target.value
    this.setState({
      number: PhoneInput,
    })
  }

  render() {
    const { name, number } = this.state
    return (
      <ContactAddFormEl onSubmit={handleSubmit}>
        <label>
          Name
          <input
            type="text"
            name="name"
            pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
            title="Имя может состоять только из букв, апострофа, тире и пробелов. Например Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan и т. п."
            required
            value={name}
            onChange={this.handleNameChange}
          />
        </label>
        <label>
          Number
          <input
            type="tel"
            name="tel"
            required
            value={number}
            onChange={this.handlePhoneChange}
          ></input>
        </label>
        <button>Add Contact</button>
      </ContactAddFormEl>
    )
  }
}
